import React from 'react';
import ReactApexChart from 'react-apexcharts';

const Visualization = ({ financialScore }) => {
  const chartOptions = {
    chart: {
      height: 350,
      type: 'line',
      zoom: {
        enabled: true
      }
    },
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'straight'
    },
    title: {
      text: 'Financial Health Trends by Month',
      align: 'left'
    },
    grid: {
      row: {
        colors: ['#f3f3f3', 'transparent'],
        opacity: 0.5
      },
    },
    xaxis: {
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep'],
    },
    yaxis: {
      ranges: [
        {
          from: 0,
          to: 100,
          color: '#FFD700', // Background color for the range
        }]}
  }

  const chartSeries = [{
    name: "Financial Health",
    data: [50, 80, 100, 40,50, 80, 100, 40, 90]
  }];

  return (
    <div className='visualizer'>
      <h2>Financial Health Score</h2>
      <ReactApexChart options={chartOptions} series={chartSeries} type="line" />
    </div>
  );
};

export default Visualization;
