import React from 'react';
import ReactApexChart from 'react-apexcharts';

const Visualization = ({ financialScore }) => {
  const chartOptions = {
    chart: {
      height: 350,
      type: 'line',
      zoom: {
        enabled: false
      },
      toolbar: {
        show: false // Hide the toolbar
      },
    },
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'smooth', // Use smooth curves for a more aesthetically pleasing look
      width: 2,
    },
    colors: ['#4CAF50'], // Green color for the line
    grid: {
      row: {
        colors: ['#F8F8F8'], // Light gray background color
        opacity: 1
      },
    },
    xaxis: {
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May'],
      labels: {
        style: {
          colors: '#555', // Darken x-axis labels
          fontSize: '14px',
        }
      }
    },
    yaxis: {
      labels: {
        style: {
          colors: '#555', // Darken y-axis labels
          fontSize: '14px',
        }
      }
    },
    title: {
      text: 'Financial Health Score',
      align: 'left',
      style: {
        fontSize: '18px',
        fontWeight: 'bold',
        color: '#333', // Darken title color
      }
    },
  };

  const chartSeries = [{
    name: "Financial Health",
    data: [0, 50, 80, 100]
  }];

  return (
    <div className='visualizer'>
      <ReactApexChart options={chartOptions} series={chartSeries} type="line" />
    </div>
  );
};

export default Visualization;
