import React from 'react';
import ReactApexChart from 'react-apexcharts';

const Visualization = ({ financialScore }) => {
  const chartOptions = {
    chart: {
      type: 'line',
      zoom: {
        enabled: false
      },
      tooltip: {
        enabled: false,
      },
      toolbar: {
        show: false // Hide the toolbar
      },
    },
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'straight', // Use smooth curves for a more aesthetically pleasing look
      width: 3,
    },
    grid: {
      row: {
        colors: ['#E3E3E3', '#fff'],
        opacity: 0.2
      },
    },
    xaxis: {
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May'],
    },
    yaxis: {
      min: 0,
      max: 100,
      labels: {
        style: {
          colors: '#fff', // Darken y-axis labels
          fontSize: '14px',
        }
      },
      tickAmount: 5,
    }
  }

  const chartSeries = [{
    name: "Financial Health",
    data: [0, 50, 80, 90]
  }];

  return (
    <div className='visualizer'>
      <h2>Financial Health Graph</h2>
      <ReactApexChart options={chartOptions} series={chartSeries} type="line" />
    </div>
  );
};

export default Visualization;
